and edo.CNFA_YN=#{cnfaYn}
and cmu.USER_NM=#{userNm}
and cmu.USER_ID=#{userId}
and edo.EDCT_NM LIKE CONCAT('%', #{edctNm}, '%')
order by edo.APLC_TS desc
Limit #{pageSet} ,10

