mapper.select:
select cmu.USER_ID as userId -- 직원번호 ,cmu.USER_NM as userNm -- 직원명 ,cmu.USER_JTM as userJtm -- 직책 ,cmu.USER_TPN as userTpn -- 행내전화 ,cmb.BRCD as brcd -- 부서코드 ,cmb.BRNM as brnm -- 부서명 ,cmu.TEAM_CD as teamCd-- 팀코드 ,(select BRNM from itep.tb_iep_cmb001m where cmu.TEAM_CD = itep.tb_iep_cmb001m.BRCD) as teamNM -- 팀명 ,cmu.USE_YN as useYn -- 퇴직여부 ,cla.ATHR_CD as athrCd -- 권한코드 ,cla.ATHR_NM as athrNm -- 권한명 from itep.tb_iep_cmu001m cmu, itep.tb_iep_cmb001m cmb, itep.tb_iep_cla001c cla where cmu.DVCD = cmb.BRCD and cmu.ATHR_CD = cla.ATHR_CD and cmb.use_yn = 'Y' and cla.use_yn = 'Y'
and cmu.USER_ID = #{userId}
and cmu.USER_NM = #{userNm}
and cmu.DVCD = #{brcd}
and cmu.ATHR_CD =#{athrCd}
Limit #{pageSet} ,10

select cmu.USER_ID as userId -- 직원번호 ,cmu.USER_NM as userNm -- 직원명 ,cmu.USER_JTM as userJtm -- 직책 ,cmu.USER_TPN as userTpn -- 행내전화 ,cmb.BRNM as brnm -- 부서명 ,cla.ATHR_NM as athrNm -- 권한명 from itep.tb_iep_cmu001m cmu, itep.tb_iep_cmb001m cmb, itep.tb_iep_cla001c cla where cmu.DVCD = cmb.BRCD and cmu.ATHR_CD = cla.ATHR_CD and cmb.use_yn = 'Y' and cla.use_yn = 'Y'
and cmu.USER_ID = #{userId}
and cmu.USER_NM = #{userNm}
and cmu.DVCD = #{brcd}
and cmu.ATHR_CD =#{athrCd};

mapper.update:

update	itep.tb_iep_cmu001m cmu 
set 	cmu.ATHR_CD=#{athrCd}
where 	cmu.USER_ID=#{userId}
