mapper.select:
/*교육목록조회*/ SELECT edm.EDCT_ID as edctId #교육메인ID , cld.EDCT_CLSF_NM as edctClsfNm #교육분류 , edm.EDCT_NM as edctNm #교육명 , cli.EDIN_NM as edinNm #교육기관명 FROM itep.tb_iep_edm001m edm , itep.tb_iep_cld001c cld , itep.tb_iep_cli001c cli where edm.EDCT_CLSF_CD = cld.EDCT_CLSF_CD and edm.EDIN_CD = cli.EDIN_CD and edm.USE_YN = 'Y' # 삭제 안된 교육만 show
and edm.EDCT_CLSF_CD = #{edctClsfCd} # 조회조건 : 교육 분류
and edm.EDCT_NM like concat('%',#{edctNm},'%') # 조회조건 : 교육 명칭

mapper.update:

update	itep.tb_iep_edm001m edm 
set 	edm.USE_YN='N'
where 	edm.EDCT_ID=#{edctId}

mapper.select:
/*과정 개설 신청 현황 TB_IEP_EDO001M 전체 검색*/ /*부서명, 직원번호, 직원명, 과목명, 교육기관, 학습기간, 상세확인*/ /* 기본 조회는 전체가 아닌 처리해야될 대상이 먼저 나오느것으로 */ SELECT edo.APLC_ID as aplcID # 과정개설신청 ID ,cmb.BRNM as brnm # 부서명 ,edo.USER_ID as userID # 직원번호 ,cmu.USER_NM as userNm # 직원명 ,edo.EDCT_NM as edctNm # 교육명 ,edo.EDIN_NM as edinNm # 교육기관명 ,edo.EDCT_STTG_YMD as edctSttgYmd # 교육시작일 ,edo.EDCT_FNSH_YMD as edctFnshYmd # 교육종료일 from itep.tb_iep_edo001m edo # 테이블 : 과정개설신청 ,itep.tb_iep_cmu001m cmu # 테이블 : 유저정보 ,itep.tb_iep_cmb001m cmb # 테이블 : 부서정보 where edo.USER_ID = cmu.USER_ID and cmu.DVCD = cmb.BRCD
/*확인 여부 조건 검색시 추가*/ and edo.CNFA_YN='#{cnfaYn}'
/*이름 조건 검색*/ and cmu.USER_NM='#{userNm}'
AND edo.EDCT_NM like '%#{edctNm}%' # 조회조건 : 교육 명칭

SELECT edc.EDCT_CNT_ID as edctCntId # 교육 차수 ID ,edm.EDCT_CLSF_CD as edctClsfCd # 교육분류 코드 ,cld.EDCT_CLSF_NM as edctClsfNm # 교육분류 명칭 ,edc.EDCT_STTG_YMD as edctSttgYmd # 교육 시작 일자 ,edc.EDCT_FNSH_YMD as edctFnshYmd # 교육 종료 일자 ,edm.EDCT_NM as edctNm # 교육명 ,cli.EDIN_NM as edinNm # 교육기관명 ,count(eda.EDCT_APLC_ID) as edctAplcIdCnt # 수강신청 인원 FROM itep.TB_IEP_EDC001M edc # 테이블:교육차수 ,itep.TB_IEP_EDM001M edm # 테이블:교육메인 ,itep.TB_IEP_EDA001M eda # 테이블:교육신청 ,itep.TB_IEP_CLI001C cli # 테이블:기관코드 ,itep.TB_IEP_CLD001C cld # 테이블:교육분류코드 WHERE edc.EDCT_ID = edm.EDCT_ID AND edm.EDCT_CLSF_CD = cld.EDCT_CLSF_CD AND edm.EDIN_CD = cli.EDIN_CD AND edc.EDCT_CNT_ID = eda.EDCT_CNT_ID AND eda.USE_YN='Y' #신청서 USE상태 AND (eda.APLC_STG_CD = 'EDUFIN' or eda.APLC_STG_CD = 'APRFIN') #신청서가 승인 완료거나 교육 완료인 상태
/*없지만 명칭조회도 생길듯함*/ AND edm.EDCT_NM like '%자바%' /*edm.EDCT_NM like '%aix%'*/
/*기간 조회 조회시 입력시작일 부터 입력종료일+1일*/ AND ( (edc.EDCT_STTG_YMD >= '#{startDate}' and edc.EDCT_STTG_YMD < '#{endDate}') or (edc.EDCT_FNSH_YMD >= '#{startDate}' and edc.EDCT_FNSH_YMD < '#{endDate}') )
/*교육분류조회*/ AND edm.EDCT_CLSF_CD = #{edctClsfCd}
AND edm.EDCT_NM like '%#{edctNm}%' # 조회조건 : 교육 명칭
GROUP BY edc.EDCT_CNT_ID;

mapper.update:

				UPDATE TB_IEP_EDO001M
				SET CNFA_YN = 'Y'
     				, CNFM_ID = #{cnfmId}
				WHERE APLC_ID = #{aplcId};


UPDATE TB_IEP_EDA001M
SET CTCR_YN = #{ctcrYn}
WHERE EDCT_APLC_ID = #{edctAplcId}
AND USE_YN = 'Y';


UPDATE TB_IEP_EDC001M
SET FNSH_YN = 'Y'
WHERE EDCT_CNT_ID = #{edctCntId}


UPDATE TB_IEP_EDA001M 
SET RFRC_CON = #{내용}
WHERE EDCT_APLC_ID = #{교육신청ID};


update	itep.tb_iep_cmu001m cmu 
set 	cmu.ATHR_CD=#{athrCd}
where 	cmu.USER_ID=#{userId}
