mapper.select:
/*교육신청>교육목록>교육목록조회*/ SELECT C.EDCT_CNT_ID AS EDCTCNTID #교육차수ID ,M.EDCT_NM AS EDCTNM #교육명 ,M.EDIN_CD AS EDINCD #교육기관코드 ,(SELECT EDIN_NM FROM TB_IEP_CLI001C I WHERE I.EDIN_CD = M.EDIN_CD LIMIT 1) AS EDINNM #교육기관명 ,M.EDCT_CLSF_CD AS EDCTCLSFCD #교육분류코드 ,(SELECT EDCT_CLSF_NM FROM TB_IEP_CLD001C D WHERE D.EDCT_CLSF_CD = M.EDCT_CLSF_CD LIMIT 1) AS EDCTCLSFNM #교육뷴류명 ,M.EDCT_LEVL AS EDCTLEVL #교육수준 ,C.APLC_STTG_YMD AS aplcSttgYmd #학습시작시간 ,C.APLC_FNSH_YMD AS aplcFnshYmd #학습종료시간 FROM TB_IEP_EDM001M M, (SELECT EDCT_CNT_ID,EDCT_ID, EDCT_STTG_YMD ,EDCT_FNSH_YMD, APLC_STTG_YMD, APLC_FNSH_YMD, CNCL_STTG_YMD, CNCL_FNSH_YMD, EDCT_STTG_TIM, EDCT_FNSH_TIM FROM TB_IEP_EDC001M WHERE EDCT_CNT_ID IN (SELECT MAX(EDCT_CNT_ID) FROM TB_IEP_EDC001M WHERE USE_YN='Y' GROUP BY EDCT_ID )) C WHERE 1=1 AND M.EDCT_ID = C.EDCT_ID AND M.USE_YN !='N'
AND M.EDCT_NM LIKE CONCAT('%',#{edctNm},'%')
AND M.EDIN_CD LIKE CONCAT('%',#{edinCd},'%')
AND C.EDCT_CNT_ID = #{edctCntId}
AND M.EDCT_CLSF_CD = #{edctClsfCd}
ORDER BY C.APLC_STTG_YMD DESC LIMIT 100

mapper.insert:

/*교육신청>과정개설신청>과정개설신청정보등록*/
INSERT INTO 
TB_IEP_EDO001M(
	 USER_ID	#사용자ID
	,APLC_TS	#신청일시
	,INBK_EDCT_YN	#행내교육여부
	,EDCT_NM	#교육명 
	,EDCT_CON	#교육내용 및 신청사유
	,EDIN_NM	#교육기관명
	,EDCT_STTG_YMD	#학습시작일 
	,EDCT_FNSH_YMD	#학습종료일 
	,APLC_STTG_YMD	#신청시작일
	,APLC_FNSH_YMD	#신청종료일 
	,EDCT_STTG_TIM	#교육시작시간 
	,EDCT_FNSH_TIM	#교육종료시간 
	,EDEX	#교육비용 
	,ONL_EDCT_YN	#온라인교육여부
	,EDCT_LEVL	#교육수준 
	,APND_DAT	#첨부파일 
	,CNFA_YN	#확인여부
	,CNFM_ID	#확인자ID
	,USE_YN	#사용여부
) VALUES(
	 #{userId}
	,NOW()
	,#{inbkEdctYn}
	,#{edctNm}
	,#{edctCon}
	,#{edinNm}
	,#{edctSttgYmd}
	,#{edctFnshYmd}
	,#{aplcSttgYmd}
	,#{aplcFnshYmd}
	,#{edctSttgTim}
	,#{edctFnshTim}
	,#{edex}
	,#{onlEdctYn}
	,#{edctLevl}
	,#{apndDat}
	,'N'
	,NULL
	,'Y'
)

mapper.select:
/*게시판>공지사항>공지사항조회*/ SELECT PBNS_ID AS PBNSID#공지사항ID ,EDCT_CLSF_CD AS EDCTCLSFCD #교육분류코드 ,(SELECT EDCT_CLSF_NM FROM TB_IEP_CLD001C D WHERE D.EDCT_CLSF_CD = N.EDCT_CLSF_CD LIMIT 1 ) AS EDCTCLSFNM #교육기관명 ,TTL AS TTL#제목 ,CON AS CON#내용 ,RGSN_TS AS RGSNTS#등록일시 ,USER_ID AS USERID#사용자ID ,(SELECT USER_NM FROM TB_IEP_CMU001M U WHERE U.USER_ID = N.USER_ID LIMIT 1 ) AS USERNM #신청자명 ,APND_DAT AS APNDDAT #첨부파일 ,USE_YN AS USEYN #사용여부 FROM TB_IEP_BDN001M N WHERE 1=1
AND TTL LIKE CONCAT('%',#{ttl},'%')
AND USE_YN ='Y'

mapper.delete:

/*게시판>공지사항>공지사항삭제*/
UPDATE TB_IEP_BDN001M
set USE_YN ='N'	#사용여부
WHERE PBNS_ID = #{pbnsId}

mapper.select:
/*게시판>자료실>자료실조회*/ SELECT RFLB_ID AS RFLBID#공지사항ID ,EDCT_CLSF_CD AS EDCTCLSFCD#교육분류코드 ,(SELECT EDCT_CLSF_NM FROM TB_IEP_CLD001C D WHERE D.EDCT_CLSF_CD = EDCT_CLSF_CD) AS EDCTCLSFNM #교육기관명 ,TTL AS TTL#제목 ,CON AS CON#내용 ,RGSN_TS AS RGSNTS#등록일시 ,USER_ID AS USERID#사용자ID ,(SELECT USER_NM FROM TB_IEP_CMU001M U WHERE U.USER_ID = USER_ID) AS USERNM #신청자명 ,APND_DAT AS APNDDAT #첨부파일 ,USE_YN AS USEYN #사용여부 FROM TB_IEP_BDR001M WHERE 1=1
AND TTL LIKE CONCAT('%',#{ttl},'%')
AND USE_YN ='Y'

mapper.delete:

/*게시판>자료실>자료실삭제*/
UPDATE TB_IEP_BDR001M
set USE_YN ='N'	#사용여부
WHERE RFLB_ID = #{rflbId}
