mapper.update:

/*수강신청 취소*/
UPDATE TB_IEP_EDA001M 
SET APLC_STG_CD = 'CANCEL'
WHERE EDCT_APLC_ID = #{edctAplcId}

mapper.select:
/*수강완료교육 조회*/ SELECT EDA.EDCT_APLC_ID AS edctAplcId, -- 교육신청id EDM.EDCT_ID AS edctId, -- 교육id EDA.EDCT_CNT_ID AS edctCntId, -- 차수id EDA.USER_ID AS userId, -- 신청자명 EDM.EDCT_NM AS edctNm, -- 교육명 CLI.EDIN_NM AS edinNm, -- 교육기관명 DATE_FORMAT(EDC.EDCT_STTG_YMD,'%Y.%m.%d') AS edctSttgYmd, -- 학습시작일 DATE_FORMAT(EDC.EDCT_FNSH_YMD,'%Y.%m.%d') AS edctFnshYmd, -- 학습종료일 IF(EDA.CTCR_YN = 'Y','수료','미수료') AS ctcrYn -- 수료여부 FROM TB_IEP_EDA001M EDA, TB_IEP_EDC001M EDC, TB_IEP_EDM001M EDM, TB_IEP_CLI001C CLI WHERE 1=1 AND EDA.EDCT_CNT_ID = EDC.EDCT_CNT_ID AND EDC.EDCT_ID = EDM.EDCT_ID AND EDM.EDIN_CD = CLI.EDIN_CD AND EDA.USER_ID = #{userId} AND EDA.APLC_STG_CD = 'EDUFIN' AND EDA.USE_YN = 'Y'
AND EDC.EDCT_STTG_YMD BETWEEN CONCAT(#{sttgYmd}, ' 00:00:00') AND CONCAT(#{fnshYmd}, ' 23:59:59')
ORDER BY EDC.EDCT_STTG_YMD DESC

/*수강신청이력 조회*/ SELECT EDA.EDCT_APLC_ID AS edctAplcId, -- 교육신청id EDM.EDCT_ID AS edctId, -- 교육id EDA.EDCT_CNT_ID AS edctCntId, -- 차수id EDA.USER_ID AS userId, -- 신청자명 EDM.EDCT_NM AS edctNm, -- 교육명 CLI.EDIN_NM AS edinNm, -- 교육기관명 DATE_FORMAT(EDC.EDCT_STTG_YMD,'%Y.%m.%d') AS edctSttgYmd, -- 학습시작일 DATE_FORMAT(EDC.EDCT_FNSH_YMD,'%Y.%m.%d') AS edctFnshYmd, -- 학습종료일 IF(EDC.FNSH_YN = 'Y','차수종료','') AS fnshYn, -- 차수종료여부 CLS.APLC_STG_NM AS aplcStgNm -- 신청단계명 FROM TB_IEP_EDA001M EDA, TB_IEP_EDC001M EDC, TB_IEP_EDM001M EDM, TB_IEP_CLI001C CLI, TB_IEP_CLS001C CLS WHERE 1=1 AND EDA.EDCT_CNT_ID = EDC.EDCT_CNT_ID AND EDC.EDCT_ID = EDM.EDCT_ID AND EDM.EDIN_CD = CLI.EDIN_CD AND EDA.APLC_STG_CD = CLS.APLC_STG_CD AND EDA.USER_ID = #{userId}
AND EDC.EDCT_STTG_YMD BETWEEN CONCAT(#{sttgYmd}, ' 00:00:00') AND CONCAT(#{fnshYmd}, ' 23:59:59')
AND EDA.USE_YN='Y' ORDER BY EDC.EDCT_STTG_YMD DESC
